// Extract data from webhook
const body = JSON.parse($json.body);

return {
  json: {
    fileId: body.fileId,
    fileName: body.fileName,
    content: body.content,
    folderPath: body.folderPath,
    lastModified: body.lastModified,
    timestamp: body.timestamp,
    trigger: body.trigger || 'manual'
  }
};

// Determine file path in Git repo
const fileName = $json.fileName;
const folderPath = $json.folderPath;

// Map Google Drive path to Git path
// Example: "AI Knowledge Base/Products" -> "01-knowledge-base/products"
const pathMapping = {
  'Products': '01-knowledge-base/products',
  'Marketing': '01-knowledge-base/marketing',
  'Procedures': '01-knowledge-base/procedures',
  'FAQs': '01-knowledge-base/faqs'
};

// Extract category from folder path
const category = folderPath.split('/').pop();
const gitPath = pathMapping[category] || '01-knowledge-base/misc';

// Ensure .md extension
const safeFileName = fileName.endsWith('.md') ? fileName : `${fileName}.md`;

return {
  json: {
    ...item.json,
    gitPath: `${gitPath}/${safeFileName}`,
    category: category
  }
};

// Write content to file
const fs = require('fs');
const path = require('path');

const repoPath = '/path/to/repo';
const filePath = path.join(repoPath, $json.gitPath);
const content = $json.content;

// Ensure directory exists
const dir = path.dirname(filePath);
if (!fs.existsSync(dir)) {
  fs.mkdirSync(dir, { recursive: true });
}

// Write file
fs.writeFileSync(filePath, content, 'utf8');

return {
  json: {
    ...item.json,
    fullPath: filePath,
    written: true
  }
};

// Log sync activity
const logEntry = {
  timestamp: new Date().toISOString(),
  file: $json.fileName,
  gitPath: $json.gitPath,
  trigger: $json.trigger,
  status: 'success'
};

console.log('Sync completed:', logEntry);

return { json: logEntry };
